import React, { useEffect, useState } from 'react'
import { useAPI } from '../../hooks/api.hook';
import Spinner from '../Spinners/Spinner';

const PostPage = props => {
    const [postData, setPostData] = useState({});
    const { loading, request } = useAPI();
    const postUrl = props.match.params.url;
    useEffect(async () => {
        const response = await request('/api/posts/get/review-data', 'POST', JSON.stringify({ url: postUrl }));
        setPostData(response[0]);
    }, [postUrl]);
    if(loading) return <Spinner />
    return (
        <div className="card h-100">
            <div className="card-header">
            <h2>{postData.title}</h2>
            </div>
            <div className="card-body">
                {postData.content}
            </div>
            <ul className="list-group list-group-flush">
                {postData.organisation ? <li className="list-group-item">{postData.organisation}</li> : ''}
                {postData.address ? <li className="list-group-item">{postData.address}</li> : ''}
                {postData.phone ? <li className="list-group-item">{postData.phone}</li> : ''}
                {postData.email ? <li className="list-group-item">{postData.email}</li> : ''}
                {postData.site ? <li className="list-group-item">{postData.site}</li> : ''}
                {postData.tags ? <li className="list-group-item">{postData.tags}</li> : ''}
            </ul>
        </div>
    )
}

export default PostPage;
