import React from 'react';
import Spinner from '../components/Spinners/Spinner';
import { HIDE_NOTIFICATION, NOTIFICATION } from './types';

const initialState = {
    isShow: false,
    title: 'Уведомление',
    content: <Spinner />
}

export const modalReducer = (state = initialState, action) => {
    switch(action.type){
        case NOTIFICATION:
            return {
                ...state,
                ...action.payload,
                isShow: true
            }
        case HIDE_NOTIFICATION:
            return {
                ...state,
                isShow: false,
                title: 'Уведомление',
                content: <Spinner />

            }
        default:
            return state
    }
}
