import React, { useEffect, useState } from 'react'
import { useAPI } from '../../hooks/api.hook';

const CommentCards = () => {
    const { loading, error,  request } = useAPI();
    const [ comments, setComments ] = useState({});

    useEffect(async ()=>{
        const response = await request('/api/posts/comments', 'POST', JSON.stringify({ postId: "5f9688b3517561499c3f38f9" }));

        let graf = new Map(response.map(comm => [comm._id, {...comm, used: false}]));
        console.log(graf);
        const use = id => graf.set(id, {...graf.get(id), used: true});
        const isUsed = id => graf.get(id).used;
        const dfs = id => {
            use(id);
            console.log(graf.get(id));
            for(let v in graf.get(id).replays.length) {
                if(!isUsed(v)) dfs(v);
            }
        }
        dfs("5f96b37c4597005aa4ef3cb8");

    }, []);

    return (
        <div>
            
        </div>
    )
}

export default CommentCards;
