import React, { useState } from 'react'
import { useDispatch } from 'react-redux';
import { notification } from '../../redux/actions';
import { useAPI } from '../../hooks/api.hook';

const CommentForm = ({ post }) => {
    const dispatch = useDispatch();
    const [isValid,setValid] = useState(false);
    const { request } = useAPI();
    const [formValues, setFormValues] = useState({
        name: '',
        content: ''
    });
    const validation = async (event) =>{
        event.preventDefault();
        if (event.target.checkValidity() === false) event.stopPropagation();
        else{
            dispatch(notification({isShow: true}));
            const response = await request('/api/posts/add/comment', 'POST', JSON.stringify({ ...formValues, postId: post._id }));
            dispatch(notification({
                title: response.type === "success" ? 'Уведомление' : 'Ошибка',
                content: response.message
            }));
        }
        setValid(true);
    }
    const handleChange = event => {
        setFormValues(formValues => ({
            ...formValues,
            [event.target.name]: event.target.value
        })); 
    }
    return (
        <div className="card mt-4">
            <h5 className="card-header">Оставить комментарий</h5>
            <div className="card-body">
            <form className={`needs-validation ${isValid ? "was-validated" : ""}`} noValidate onSubmit={validation}>
                <div className="form-group">
                    <label for="name">Ваше имя</label>
                    <input type="text" className="form-control" id="name" name="name" required onChange={ handleChange } />
                    <small id="emailHelp" class="form-text text-muted">Будет отображаться рядом с комментарием</small>
                </div>
                <div className="form-group">
                    <label for="content">Комментарий</label>
                    <textarea className="form-control" id="content" name="content" required onChange={ handleChange }/>
                </div>
                <div className="form-group mt-2">
                    <div className="form-check">
                        <input className="form-check-input" type="checkbox" value="" id="invalidCheck" required />
                        <label className="form-check-label" htmlFor="invalidCheck">
                            Я согласен с <a href="/rules">правилами сайта</a>
                        </label>
                        <div className="invalid-feedback">
                            Вы должны согласиться с условиями, чтобы отправить форму
                        </div>
                    </div>
                </div>
                <button type="submit" className="btn btn-primary mt-2">Отправить</button>
            </form>
            </div>
        </div>
    )
}

export default CommentForm;
