import { LOAD_REVIEWS } from './types';
const initialState = {
    reviews: [],
    complaints: []
}

export const postsReducer = (state = initialState, action) => {
    switch(action.type){
        case INIT:
            return { 
                     ...state,
                     reviews: state.reviews.concat(action.payload.reviews),
                     complaints: state.complaints.concat(action.payload.complaints)
                    };
        case LOAD_REVIEWS:
            return {
                ...state,
                reviews: state.reviews.concat(action.payload)
            };
        default:
            return state;
    }
}
