import React, { useState } from 'react';

const Modal = (props) => {
    const [hidden, setHidden] = useState(props.show);
    const close = () => props.show = false;
    return (
        <div className={`modal fade ${props.show && "show"}`} tabIndex="-1" style={{display: `${props.show ? "block" : "none"}`}}>
            <div className="modal-dialog modal-dialog-centered">
                <div className="modal-content">
                <div className="modal-header">
                    <h5 className="modal-title">Modal title</h5>
                    <button type="button" className="close" data-dismiss="modal" aria-label="Close" onClick={close}>
                    <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div className="modal-body">
                    {props.children}
                </div>
                <div className="modal-footer">
                    <button type="button" className="btn btn-secondary" data-dismiss="modal" onClick={close}>Close</button>
                    <button type="button" className="btn btn-primary">Save changes</button>
                </div>
                </div>
            </div>
        </div>
    )
}

export default Modal;
