import React, { useState } from 'react';

const menuItems = [
    {text: 'Жалобы', link: '/'},
    {text: 'Рекомендации', link: '/'},
    {text: 'Информация', link: '/about'}
 ];
const Header = props => {
    const [menuItemActive, setMenuItem] = useState(menuItems[0]);
    const _handleClick = (menuItem) => {
        setMenuItem(menuItem);
    }
    return(
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
            <a className="navbar-brand" href="/">Жалобы и отзывы</a>
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarSupportedContent">
                <ul className="navbar-nav mr-auto">
                    {menuItems.map(menuItem => (
                        <li className={`nav-item ${menuItem.text === menuItemActive.text ? "active" : ""}`} onClick={_handleClick.bind(this, menuItem)}>
                            <a className="nav-link" href={menuItem.link}>{menuItem.text}</a>
                        </li>
                    ))}
                </ul>
                <form className="form-inline my-2 my-lg-0">
                    <input className="form-control mr-sm-2" type="search" placeholder="Поиск" aria-label="Поиск" />
                    <button className="btn btn-outline-primary my-2 my-sm-0" type="submit">Найти</button>
                </form>
            </div>
        </nav>
    );
}

export default Header;
