import React from 'react';
import Spinner from '../components/Spinners/Spinner';
import { NOTIFICATION } from './types';

const initialState = {
    isShow: false,
    title: 'Уведомление',
    content: <Spinner />
}

export const modalReducer = (state = initialState, action) => {
    switch(action.type){
        case NOTIFICATION:
            return {
                ...state,
                ...content
            }
    }
}
